import React, { useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import "./styles/SignUp.css";

function ListItem({ index, onDelete }) {
	const [source, setSource] = useState("news");
	const [text, setText] = useState("");

	const handleSourceChange = (e) => {
		setSource(e.target.value);
	};

	const handleTextChange = (e) => {
		setText(e.target.value);
	};

	const handleDelete = () => {
		onDelete(index);
	};

	let prefix = ""; // Default prefix
	if (source === "reddit") {
		prefix = "r/";
	} else if (source === "twitter") {
		prefix = "@";
	}

	return (
		<div className="list-item">
			<select name="source[]" value={source} onChange={handleSourceChange}>
				<option value="news">News</option>
				<option value="reddit">Reddit</option>
				<option value="twitter">Twitter</option>
			</select>
			<input
				type="text"
				name="text[]"
				placeholder="Enter text"
				value={text}
				onChange={handleTextChange}
			/>
			<span className="delete-button" onClick={handleDelete}>
				<img src="trash-can.svg" />
			</span>
			<span className="prefix">{prefix}</span> {/* Display the prefix */}
		</div>
	);
}

function SignUp() {
	const [listItems, setListItems] = useState([{ id: 0, index: 0 }]);

	const handleAddButton = () => {
		setListItems((prevListItems) => [
			...prevListItems,
			{ id: prevListItems.length, index: prevListItems.length },
		]);
	};

	const handleDelete = (index) => {
		setListItems((prevListItems) =>
			prevListItems.filter((item) => item.index !== index)
		);
	};

	const handleSubmit = async (event) => {
		event.preventDefault();

		// Prepare the data to be sent
		const formData = new FormData(event.target);
		const data = {};
		for (let [name, value] of formData.entries()) {
			data[name] = value;
		}

		// Send the data to the REST API
		try {
			const response = await fetch("https://example.com/api/endpoint", {
				method: "POST",
				headers: {
					"Content-Type": "application/json",
				},
				body: JSON.stringify(data),
			});

			if (response.ok) {
				// Handle success response
				console.log("Data sent successfully");
				// Reset the form or perform any other actions
			} else {
				// Handle error response
				console.error("Error sending data");
			}
		} catch (error) {
			// Handle network error
			console.error("Network error:", error);
		}
	};

	return (
		<form id="my-form" onSubmit={handleSubmit}>
			<div id="list-container">
				{listItems.map((item) => (
					<ListItem key={item.id} index={item.index} onDelete={handleDelete} />
				))}
			</div>
			<button type="button" id="add-button" onClick={handleAddButton}>
				<FontAwesomeIcon icon={faPlus} />
			</button>
			<button type="submit">Submit</button>
		</form>
	);
}

export default SignUp;
