import { NavLink } from "react-router-dom";
import "./styles/Signup.css";
import TextList from "../Components/TextList";
import Enum from "../Components/Enum";
import React, { useState } from "react";

function ListItem() {
	const [source, setSource] = useState("news");
	const [text, setText] = useState("");

	const handleSourceChange = (e) => {
		setSource(e.target.value);
	};

	const handleTextChange = (e) => {
		setText(e.target.value);
	};

	const handleDelete = () => {
		// Implement your delete logic here
	};

	return (
		<div className="list-item">
			<select name="source[]" value={source} onChange={handleSourceChange}>
				<option value="news">News</option>
				<option value="reddit">Reddit</option>
				<option value="twitter">Twitter</option>
			</select>
			<input
				type="text"
				name="text[]"
				placeholder="Enter text"
				value={text}
				onChange={handleTextChange}
			/>
			<span className="delete-button" onClick={handleDelete}>
				<img src="trash-can.svg" alt="Delete" />
			</span>
		</div>
	);
}

function Signup() {
	const enumValues = ["Option 1", "Option 2", "Option 3"];

	const handleEnumChange = (event) => {
		console.log("Selected value:", event.target.value);
	};
	const [listItems, setListItems] = useState([]);

	const handleAddButton = () => {
		setListItems([...listItems, <ListItem key={listItems.length} />]);
	};

	const handleSubmit = (event) => {
		event.preventDefault();

		// Retrieve form data and perform necessary actions
		const formData = new FormData(event.target);
		for (const pair of formData.entries()) {
			console.log(pair[0] + ": " + pair[1]);
		}

		// Additional logic for submitting the form and handling the data on the server-side can be implemented here.
		// You'll need to write server-side code to handle form submission and store user information securely.
	};

	return (
		<div class="signup">
			<div class="form-container">
				<p class="already-signed-in">
					Already have an account? <a href="sign-in.html">Sign in</a>
				</p>
				<form class="signup-form" id="my-form" action="submit.html">
					<div class="name-wrapper">
						<input type="text" placeholder="First Name" name="firstName" />
						<input type="text" placeholder="Last Name" name="lastName" />
					</div>
					<label>Interests</label>
					<textarea
						placeholder="Interests"
						name="interests"
						style={{ height: "200px" }}
					></textarea>

					<label>Content Sources</label>

					<div id="list-container">
						{/* <div class="list-item">
							<select name="source[]">
								<option value="news">News</option>
								<option value="reddit">Reddit</option>
								<option value="twitter">Twitter</option>
							</select>
							<input type="text" name="text[]" placeholder="Enter text" />
							<span class="delete-button">
								<img src="trash-can.svg" alt="Delete" />
							</span>
						</div> */}
						<form id="my-form" onSubmit={handleSubmit}>
							<div id="list-container">{listItems.map((item) => item)}</div>
							<button id="add-button" type="button" onClick={handleAddButton}>
								+
							</button>
						</form>
					</div>
					<div class="button-wrapper">
						<input type="submit" value="Sign Up" />
					</div>
				</form>
			</div>
		</div>
	);
}

export default Signup;
