import React, { useState } from "react";
import "./styles/Main.css";

const MainPage = () => {
	const [selectedSquare, setSelectedSquare] = useState(null);

	const flavors = [
		{
			id: 1,
			flavor: "Caramel",
			title: "Business",
			description: "General information about business news.",
			color: "#D4A15D",
		},
		{
			id: 2,
			flavor: "Mocha",
			title: "Arts",
			description: "General information about arts news.",
			color: "#8B5742",
		},
		{
			id: 3,
			flavor: "Hazelnut",
			title: "Technology",
			description: "General information about technology news.",
			color: "#B38967",
		},
		{
			id: 4,
			flavor: "Cinnamon",
			title: "Science",
			description: "General information about science news.",
			color: "#BC6B38",
		},
		{
			id: 5,
			flavor: "Pumpkin Spice",
			title: "Health",
			description: "General information about health news.",
			color: "#A15233",
		},
		{
			id: 6,
			flavor: "Vanilla",
			title: "Lifestyle",
			description: "General information about lifestyle news.",
			color: "#D5C2B1",
		},
	];

	return (
		<div className="main-container">
			{/* Left-aligned greeting */}
			<h1>Hello, User!</h1>

			{/* 2x3 grid of squares */}
			<div className="grid">
				{flavors.map((flavor) => (
					<div
						key={flavor.id}
						className={`square ${
							selectedSquare === flavor.id ? "selected" : ""
						}`}
						style={{ backgroundColor: flavor.color }}
						onClick={() => setSelectedSquare(flavor.id)}
					>
						<div className="content">
							<h3>{flavor.title}</h3>
							<p>{flavor.description}</p>
						</div>
						<div className="flavor">{flavor.flavor}</div>
					</div>
				))}
			</div>
		</div>
	);
};

export default MainPage;
