import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./styles/UserView.css";

const UserView = (isSignedIn) => {
	const [firstName, setFirstName] = useState("Michael");
	const [hoveredSquare, setHoveredSquare] = useState(null);
	let navigate = useNavigate();

	fetch("http://127.0.0.1:5000/v1/user", {
		method: "get",
		headers: {
			Accept: "application/json",
			"Content-Type": "application/json",
		},
	})
		.then((response) => response.json())
		.then((response) => {
			console.log(response);
		});
	const preferences = [
		{
			title: "Mocha",
			uuid: "1b9d6bcd-bbfd-4b2d-9b5d-ab8dfbbd4bed",
			personality: "creative and imaginative",
			interests: ["arts", "culture", "entertainment"],
			contentSources: ["npr.org", "theatlantic.com", "newyorker.com"],
			color: { hex: "#8B5742" },
		},
		{
			title: "Hazelnut",
			uuid: "3b6eb659-da6a-436d-a9c2-22d8fcbdfaae",
			personality: "logical and analytical",
			interests: ["tech", "gadgets", "AI"],
			contentSources: ["wired.com", "techcrunch.com", "engadget.com"],
			color: { hex: "#B38967" },
		},
		{
			title: "Cinnamon",
			uuid: "4b9d56b1-fbdd-40b2-a955-abfddc4d0def",
			personality: "curious and intellectual",
			interests: ["science", "space", "environment"],
			contentSources: [
				"sciencedaily.com",
				"livescience.com",
				"nationalgeographic.com",
			],
			color: { hex: "#BC6B38" },
		},
		{
			title: "Pumpkin Spice",
			uuid: "8b9d6bb7-ddfc-4b2d-ab95-ab8dfbbd4bed",
			personality: "compassionate and caring",
			interests: ["health", "wellness", "nutrition"],
			contentSources: ["healthline.com", "webmd.com", "medicalnewstoday.com"],
			color: { hex: "#A15233" },
		},
		{
			title: "Vanilla",
			uuid: "7b9d6bb5-ccfc-4b2d-a955-ab8dfbbd4bed",
			personality: "warm and lighthearted",
			interests: ["lifestyle", "food", "travel"],
			contentSources: [
				"bonappetit.com",
				"condenasttraveler.com",
				"realSimple.com",
			],
			color: { hex: "#D5C2B1" },
		},
	];

	const handleSquareClick = (preference) => {
		console.log(preference);
		let path = preference.title.toLowerCase();
		if (path === "pumpkin spice") {
			path = "pumpkin-spice";
		}
		navigate(path, { state: { preference: preference } });
	};

	const handleSquareHover = (uuid) => {
		setHoveredSquare(uuid);
	};

	const handleSquareLeave = () => {
		setHoveredSquare(null);
	};

	return (
		<div className="main-container">
			<div className="main">
				{/* Left-aligned greeting */}
				<h1 className="greeting">Hello, {firstName}!</h1>
				<h1 className="welcome">Welcome to your Profile</h1>

				{/* 2x3 grid of squares */}
				<div className="grid">
					{preferences.map((preference) => (
						<div
							key={preference.uuid}
							className={`square ${
								hoveredSquare === preference.uuid ? "hovered" : ""
							}`}
							style={{
								backgroundColor:
									hoveredSquare === preference.uuid
										? "transparent"
										: preference.color.hex,
								color:
									hoveredSquare === preference.uuid
										? preference.color.hex
										: "#fefbf0",
								border:
									hoveredSquare === preference.uuid
										? "10px solid " + preference.color.hex
										: "10px solid transparent",
								// outlineOffset: hoveredSquare === flavor.id ? "-10px" : "0",
							}}
							onClick={() => handleSquareClick(preference)}
							onMouseEnter={() => handleSquareHover(preference.uuid)}
							onMouseLeave={handleSquareLeave}
						>
							<div className="content">
								<div className="flavor">{preference.title}</div>
							</div>
						</div>
					))}
				</div>
			</div>
		</div>
	);
};

export default UserView;
