import React, { useState } from "react";
import { BrowserRouter as Router, Route, Link } from "react-router-dom";
import "./styles/Main.css";
import Navbar from "../Components/Navbar";
import Footer from "../Components/Footer";

const Main = (isSignedIn) => {
	const [selectedSquare, setSelectedSquare] = useState(null);
	const [firstName, setFirstName] = useState("Michael");
	const [showInfo, setShowInfo] = useState(false);

	const flavors = [
		// flavors data...
	];

	const handleSquareClick = (flavorId) => {
		setSelectedSquare((prevSelectedSquare) =>
			prevSelectedSquare === flavorId ? null : flavorId
		);
		setShowInfo(true);
	};

	const onAnimationEnd = () => {
		setShowInfo(false);
	};

	return (
		<Router>
			<div>
				<Navbar backgroundColor={"#fefbf0"} isSignedIn={isSignedIn} />
				<div className="main-container">
					<div className="main">
						{/* Left-aligned greeting */}
						<h1 className="greeting">Hello, {firstName}!</h1>
						<h1 className="welcome">Welcome to your Profile</h1>

						{/* 2x3 grid of squares */}
						<div className="grid">
							{flavors.map((flavor) => (
								<Link to={`/flavor/${flavor.id}`} key={flavor.id}>
									<div
										className={`square ${
											selectedSquare === flavor.id ? "selected" : ""
										}`}
										style={{ backgroundColor: flavor.color }}
										onClick={() => handleSquareClick(flavor.id)}
									>
										<div className="content">
											<div className="flavor">{flavor.flavor}</div>
										</div>
									</div>
								</Link>
							))}
						</div>
					</div>
				</div>
				<Footer backgroundColor={"#fefbf0"} />
			</div>
		</Router>
	);
};

export default Main;
