import React, { useState } from "react";
import "./styles/Main.css";

const Main = () => {
	const [selectedSquare, setSelectedSquare] = useState(null);
	const [firstName, setFirstName] = useState("Michael");
	const [showInfo, setShowInfo] = useState(false);

	const flavors = [
		{
			id: 1,
			flavor: "Caramel",
			title: "Business",
			description: "General information about business news.",
			color: "#D4A15D",
		},
		{
			id: 2,
			flavor: "Mocha",
			title: "Arts",
			description: "General information about arts news.",
			color: "#8B5742",
		},
		{
			id: 3,
			flavor: "Hazelnut",
			title: "Technology",
			description: "General information about technology news.",
			color: "#B38967",
		},
		{
			id: 4,
			flavor: "Cinnamon",
			title: "Science",
			description: "General information about science news.",
			color: "#BC6B38",
		},
		{
			id: 5,
			flavor: "Pumpkin Spice",
			title: "Health",
			description: "General information about health news.",
			color: "#A15233",
		},
		{
			id: 6,
			flavor: "Vanilla",
			title: "Lifestyle",
			description: "General information about lifestyle news.",
			color: "#D5C2B1",
		},
	];

	const handleSquareClick = (flavorId) => {
		setSelectedSquare((prevSelectedSquare) =>
			prevSelectedSquare === flavorId ? null : flavorId
		);
		setShowInfo(true);
	};

	const onAnimationEnd = () => {
		setShowInfo(false);
	};

	return (
		<div className="main-container">
			<div className="main">
				{/* Left-aligned greeting */}
				<h1 className="greeting">Hello, {firstName}!</h1>
				<h1 className="welcome">Welcome to your Profile</h1>

				{/* 2x3 grid of squares */}
				<div className="grid">
					{flavors.map((flavor) => (
						<div
							key={flavor.id}
							className={`square ${
								selectedSquare === flavor.id ? "selected" : ""
							}`}
							style={{ backgroundColor: flavor.color }}
							onClick={() => handleSquareClick(flavor.id)}
						>
							<div className="content">
								<div className={`flavor ${showInfo ? "word-animation" : ""}`}>
									{flavor.flavor}
								</div>
								{selectedSquare === flavor.id && (
									<>
										<h3 className={`title ${showInfo ? "word-animation" : ""}`}>
											{flavor.title}
										</h3>
										<p
											className={`description ${
												showInfo ? "word-animation" : ""
											}`}
										>
											{flavor.description}
										</p>
									</>
								)}
							</div>
						</div>
					))}
				</div>
			</div>
		</div>
	);
};

export default Main;
