import React, { useState, useEffect } from "react";
import "./Loading.css";

const loadingMessages = [
	"Brewing a fresh batch of personalized content...",
	"Roasting up the perfect newsletter, just for you!",
	"Grinding through the internet to find the juiciest stories...",
	"Simmering the news to perfection...",
	"Stirring up a delightful blend of articles and insights...",
	"Whistling while we work on your customized newsletter...",
	"Steeping in your interests to serve up the best content...",
	"Pouring in the latest updates and hot topics...",
	"Infusing your inbox with a flavor-packed newsletter...",
	"Slowly roasting the news for an irresistible read...",
];

const Loading = () => {
	const [messageIndex, setMessageIndex] = useState(0);

	useEffect(() => {
		const interval = setInterval(() => {
			setMessageIndex((prevIndex) => (prevIndex + 1) % loadingMessages.length);
		}, 5000); // Adjust the delay (in milliseconds) between message changes here

		return () => {
			clearInterval(interval);
		};
	}, []);

	return (
		<div className="loading-page">
			<div className="loading-content">
				{loadingMessages.map((message, index) => (
					<h2
						key={index}
						className={`loading-message ${
							index === messageIndex ? "active loading-message-animation" : ""
						}`}
					>
						{message}
					</h2>
				))}
				<div className="loading-spinner"></div>
			</div>
		</div>
	);
};

export default Loading;
