import "./styles/Home.css";
import "../App.css";
import { NavLink } from "react-router-dom";
import moebius1 from "./images/moebius1.png";

function Home() {
	const backgroundImageStyle = {
		backgroundImage: `linear-gradient(rgba(0, 0, 0, 0.5), rgba(0, 0, 0, 0.5)), url(${moebius1})`,
		borderRadius: "10px",
		opacity: "1",
	};

	return (
		<div className="home">
			<div className="hero-section" style={backgroundImageStyle}>
				<h1>Your Daily Digest, Brewed Just How You Like It</h1>
				<p className="subscribe-text">
					<NavLink to="/sign-up" className="subscribe-button">
						Subscribe
					</NavLink>
				</p>
			</div>
		</div>
	);
}

export default Home;
