import Navbar from "../Components/Navbar";
import Footer from "../Components/Footer";
import { useParams } from "react-router-dom";
import "./styles/NewsTemplate.css";

function NewsTemplate() {
	const { flavor } = useParams();
	let title = flavor.charAt().toUpperCase() + flavor.slice(1);

	// Example text of list sources: nytimes.com,

	// Default preferences/user preferences are pulled from backend
	let preferences = {
		personality: "straightforward and serious",
		topics: ["finance", "tech", "startups"],
		sources: ["nytimes.com", "politico.com", "wsj.com"],
	};

	// When saved, we change the backend preferences
	return (
		<div>
			<Navbar backgroundColor="#fefbf0" isSignedIn={true} />
			<div className="news-template">
				<h1>Settings:</h1>
				<h2>{title}</h2>
			</div>
			<Footer backgroundColor="#fefbf0" />
		</div>
	);
}

export default NewsTemplate;
