import React, { useState } from "react";
import "./styles/Main.css";

const MainPage = () => {
	const [selectedSquare, setSelectedSquare] = useState(null);

	const flavors = [
		{
			id: 1,
			flavor: "Black",
			title: "Business",
			description: "General information about business news.",
			color: "#333",
		},
		{
			id: 2,
			flavor: "Espresso",
			title: "Arts",
			description: "General information about arts news.",
			color: "#8B4513",
		},
		// Add more flavors with their respective titles, descriptions, and colors
	];

	return (
		<div>
			{/* Left-aligned greeting */}
			<h1>Hello, User!</h1>

			{/* 2x3 grid of squares */}
			<div className="grid">
				{flavors.map((flavor) => (
					<div
						key={flavor.id}
						className={`square ${
							selectedSquare === flavor.id ? "selected" : ""
						}`}
						style={{ backgroundColor: flavor.color }}
						onClick={() => setSelectedSquare(flavor.id)}
					>
						<div className="content">
							<h3>{flavor.title}</h3>
							<p>{flavor.description}</p>
						</div>
						<div className="flavor">{flavor.flavor}</div>
					</div>
				))}

				{/* Default squares */}
				<div className="square default" style={{ backgroundColor: "#6495ED" }}>
					<div className="content">
						<h3>Default 1</h3>
						<p>Default description 1</p>
					</div>
					<div className="flavor">Default</div>
				</div>
				<div className="square default" style={{ backgroundColor: "#FF69B4" }}>
					<div className="content">
						<h3>Default 2</h3>
						<p>Default description 2</p>
					</div>
					<div className="flavor">Default</div>
				</div>
				{/* Add more default squares as needed */}
			</div>
		</div>
	);
};

export default MainPage;
