import React, { useState, useEffect } from "react";
import { useWhisper } from "@chengsokdara/use-whisper";
import "./styles/Conversation.css";
import XI_API_KEY from "../Config";
import { useLocation } from "react-router-dom";

function Conversation({ setIsSignedIn }) {
	const [isRecording, setIsRecording] = useState(false);
	const [showResponse, setShowResponse] = useState(false);
	const [response, setResponse] = useState("");
	const [userTranscript, setUserTranscript] = useState([]);
	const { state } = useLocation();
	const [newsletter, setNewsletter] = useState();
	setNewsletter(state);

	const { startRecording, stopRecording, transcript } = useWhisper({
		apiKey: "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",
	});

	const botResponse = [
		"Hi! How are you doing?",
		"Great to hear! What are you interested about?",
		"Sure, lot's going in the ol' Capital. The Supreme Court recently ruled on an important case! Did you hear about it?",
	];
	const [index, setIndex] = useState(0);
	useEffect(() => {
		if (isRecording) {
			startRecording();
		} else {
			stopRecording();
			setShowResponse(true);
			queryGPT();
		}
	}, [isRecording]);

	const queryGPT = async () => {
		// Perform the GPT query and set the response
	};

	const performGPTQuery = async (text) => {
		// Make the API call to GPT and return the response
		// You'll need to implement this part based on your chosen method for querying GPT
		// if (text) {
		// }
		return "";
	};

	useEffect(() => {
		if (transcript.text) {
			if (userTranscript == [["Reader", ""]]) {
				setUserTranscript([["Reader", transcript.text]]);
				console.log(userTranscript);
			} else {
				setUserTranscript((prevState) => [
					...prevState,
					["Reader", transcript.text],
				]);
				console.log(userTranscript);
			}
			const response = botResponse[index];
			setIndex((index + 1) % 3);
			setUserTranscript((prevState) => [...prevState, ["Bot", response]]);
			getBotTTS(response);
		}
	}, [transcript.text]);

	function getBotTTS(text) {}

	useEffect(() => {
		setIsSignedIn(true);
	}, []);

	const [newsIndex, setNewsIndex] = useState(false);

	return (
		<div className="conversation-wrapper">
			<div className="left-view">
				{newsletter.map((index, section) =>
					index % 6 ? <h1>{section}</h1> : <p>{section}</p>
				)}
			</div>
			<div className="right-view">
				{userTranscript.map((message) => (
					<p id="message">
						{message[0] + " "}: {message[1]}
					</p>
				))}
				<button
					className="recordButton"
					onClick={() => {
						setIsRecording(!isRecording);
					}}
				>
					{isRecording ? <p>Stop</p> : <p>Start</p>}
				</button>
			</div>
		</div>
	);
}

export default Conversation;
