import React, { useState } from "react";
import { useNavigate, history } from "react-router-dom";
import "./styles/Main.css";
import Navbar from "../Components/Navbar";
import Footer from "../Components/Footer";

const Main = (isSignedIn) => {
	const [firstName, setFirstName] = useState("Michael");
	const [hoveredSquare, setHoveredSquare] = useState(null);
	let navigate = useNavigate();

	const preferences = [
		{
			title: "Caramel",
			personality: "straightforward and serious",
			topics: ["finance", "tech", "startups"],
			contentSources: ["nytimes.com", "politico.com", "wsj.com"],
			color: { hex: "#d4a15d" },
		},
		{
			title: "Mocha",
			personality: "creative and imaginative",
			topics: ["arts", "culture", "entertainment"],
			contentSources: ["npr.org", "theatlantic.com", "newyorker.com"],
			color: { hex: "#8B5742" },
		},

		{
			title: "Hazelnut",
			personality: "logical and analytical",
			topics: ["tech", "gadgets", "AI"],
			contentSources: ["wired.com", "techcrunch.com", "engadget.com"],
			color: { hex: "#B38967" },
		},

		{
			title: "Cinnamon",
			personality: "curious and intellectual",
			topics: ["science", "space", "environment"],
			contentSources: [
				"sciencedaily.com",
				"livescience.com",
				"nationalgeographic.com",
			],
			color: { hex: "#BC6B38" },
		},

		{
			title: "Pumpkin Spice",
			personality: "compassionate and caring",
			topics: ["health", "wellness", "nutrition"],
			contentSources: ["healthline.com", "webmd.com", "medicalnewstoday.com"],
			color: { hex: "#A15233" },
		},

		{
			title: "Vanilla",
			personality: "warm and lighthearted",
			topics: ["lifestyle", "food", "travel"],
			contentSources: [
				"bonappetit.com",
				"condenasttraveler.com",
				"realSimple.com",
			],
			color: { hex: "#D5C2B1" },
		},
	];

	const handleSquareClick = (flavor) => {
		console.log(flavor.flavor);
		let path = flavor.flavor.toLowerCase();
		if (path === "pumpkin spice") {
			path = "pumpkin-spice";
		}
		navigate(path);
	};

	const handleSquareHover = (flavorId) => {
		setHoveredSquare(flavorId);
	};

	const handleSquareLeave = () => {
		setHoveredSquare(null);
	};

	return (
		<div>
			<Navbar backgroundColor={"#fefbf0"} isSignedIn={isSignedIn} />
			<div className="main-container">
				<div className="main">
					{/* Left-aligned greeting */}
					<h1 className="greeting">Hello, {firstName}!</h1>
					<h1 className="welcome">Welcome to your Profile</h1>

					{/* 2x3 grid of squares */}
					<div className="grid">
						{flavors.map((flavor) => (
							<div
								key={flavor.id}
								className={`square ${
									hoveredSquare === flavor.id ? "hovered" : ""
								}`}
								style={{
									backgroundColor:
										hoveredSquare === flavor.id
											? "transparent"
											: flavor.color.hex,
									color:
										hoveredSquare === flavor.id ? flavor.color.hex : "#fefbf0",
									border:
										hoveredSquare === flavor.id
											? "10px solid " + flavor.color.hex
											: "10px solid transparent",
									// outlineOffset: hoveredSquare === flavor.id ? "-10px" : "0",
								}}
								onClick={() => handleSquareClick(flavor)}
								onMouseEnter={() => handleSquareHover(flavor.id)}
								onMouseLeave={handleSquareLeave}
							>
								<div className="content">
									<div className="flavor">{flavor.flavor}</div>
								</div>
							</div>
						))}
					</div>
				</div>
			</div>
			<Footer backgroundColor={"#fefbf0"} />
		</div>
	);
};

export default Main;
