import { NavLink } from "react-router-dom";
import React, { useState } from "react";
import "./styles/Signup.css";

function ListItem({
	index,
	onDelete,
	handleSourceChange,
	handleTextChange,
	source,
	text,
}) {
	let placeholder = ""; // Default placeholder
	if (source === "news") {
		placeholder = "Ex: nytimes.com, ...";
	} else if (source === "reddit") {
		placeholder = "Ex: r/AskReddit, ...";
	} else if (source === "twitter") {
		placeholder = "Ex: @BerkeleySkydeck, ...";
	}

	const handleDelete = () => {
		onDelete(index);
	};

	return (
		<div className="list-item">
			<select name="source[]" value={source} onChange={handleSourceChange}>
				<option value="news">News</option>
				<option value="reddit">Reddit</option>
				<option value="twitter">Twitter</option>
			</select>
			<div style={{ display: "flex", alignItems: "center" }}>
				<input
					type="text"
					name="text[]"
					placeholder={placeholder}
					value={text}
					onChange={handleTextChange}
					required
				/>
			</div>
			<span className="delete-button" onClick={handleDelete}>
				<img src="trash-can.svg" alt="Delete" />
			</span>
		</div>
	);
}

function Signup() {
	const [isSignedIn, setIsSignedIn] = useState(false);
	const [firstName, setFirstName] = useState("");
	const [lastName, setLastName] = useState("");
	const [email, setEmail] = useState("");
	const [interests, setInterests] = useState("");
	const [listItems, setListItems] = useState([
		{ source: "news", text: "" }, // Initial content source item
	]);

	const handleFirstNameChange = (e) => {
		setFirstName(e.target.value);
	};

	const handleLastNameChange = (e) => {
		setLastName(e.target.value);
	};

	const handleEmailChange = (e) => {
		setEmail(e.target.value);
	};

	const handleInterestsChange = (e) => {
		setInterests(e.target.value);
	};

	const handleAddButton = () => {
		setListItems((prevListItems) => [
			...prevListItems,
			{ source: "news", text: "" }, // Add a new content source item
		]);
	};

	if (isSignedIn) {
		return <SignedInView />;
	}

	const handleDelete = (index) => {
		if (listItems.length === 1) {
			return; // Do not delete if there is only one item
		}

		setListItems((prevListItems) => {
			const updatedListItems = [...prevListItems];
			updatedListItems.splice(index, 1);
			return updatedListItems;
		});
	};

	const handleSubmit = (event) => {
		event.preventDefault();

		const formData = {
			firstName,
			lastName,
			email,
			interests: interests.split(",").map((interest) => interest.trim()),
			contentSources: listItems,
		};

		// Send the form data to the server for further processing
		console.log(formData);
	};

	return (
		<div className="signup">
			<div className="form-container">
				<p className="already-signed-in">
					Already have an account? <a href="sign-in.html">Sign in</a>
				</p>
				<form className="signup-form" onSubmit={handleSubmit}>
					<div className="name-wrapper">
						<input
							type="text"
							placeholder="First Name"
							name="firstName"
							value={firstName}
							onChange={handleFirstNameChange}
							required
						/>
						<input
							type="text"
							placeholder="Last Name"
							name="lastName"
							value={lastName}
							onChange={handleLastNameChange}
							required
						/>
					</div>
					<label>Email</label>
					<input
						type="text"
						placeholder="Email"
						name="email"
						value={email}
						onChange={handleEmailChange}
						required
					/>

					<label>Interests</label>
					<textarea
						placeholder="Tech updates, finance news, formula one..."
						name="interests"
						value={interests}
						onChange={handleInterestsChange}
						style={{ height: "200px" }}
						required
					></textarea>

					<label>Content Sources</label>
					<div className="list-container">
						{listItems.map((item, index) => (
							<ListItem
								key={index}
								index={index}
								onDelete={handleDelete}
								handleSourceChange={(e) => {
									const updatedListItems = [...listItems];
									updatedListItems[index].source = e.target.value;
									setListItems(updatedListItems);
								}}
								handleTextChange={(e) => {
									const updatedListItems = [...listItems];
									updatedListItems[index].text = e.target.value;
									setListItems(updatedListItems);
								}}
								source={item.source}
								text={item.text}
							/>
						))}
					</div>
					<div className="button-wrapper">
						<button type="button" id="add-button" onClick={handleAddButton}>
							+
						</button>
					</div>
					<div className="button-wrapper">
						<input type="submit" value="Sign Up" />
					</div>
				</form>
			</div>
		</div>
	);
}

export default Signup;
