import { NavLink } from "react-router-dom";
import React, { useState, useEffect } from "react";
import "./styles/Signup.css";
import TextList from "../Components/TextList";
import Enum from "../Components/Enum";

function ListItem({ index, onDelete }) {
	const [source, setSource] = useState("news");
	const [text, setText] = useState("");

	const handleSourceChange = (e) => {
		setSource(e.target.value);
	};

	const handleTextChange = (e) => {
		setText(e.target.value);
	};

	const handleDelete = () => {
		onDelete(index);
	};

	let prefix = ""; // Default prefix
	if (source === "reddit") {
		prefix = "r/";
	} else if (source === "twitter") {
		prefix = "@";
	}

	return (
		<div className="list-item">
			<select name="source[]" value={source} onChange={handleSourceChange}>
				<option value="news">News</option>
				<option value="reddit">Reddit</option>
				<option value="twitter">Twitter</option>
			</select>
			<div style={{ display: "flex", alignItems: "center" }}>
				<span className="prefix">{prefix}</span>
				<input
					type="text"
					name="text[]"
					placeholder="Enter text"
					value={text}
					onChange={handleTextChange}
				/>
			</div>
			<span className="delete-button" onClick={handleDelete}>
				<img src="trash-can.svg" alt="Delete" />
			</span>
		</div>
	);
}

function Signup() {
	const [firstName, setFirstName] = useState("");
	const [lastName, setLastName] = useState("");
	const [email, setEmail] = useState("");
	const [password, setPassword] = useState("");
	const [confirmPassword, setConfirmPassword] = useState("");
	const [verificationCode, setVerificationCode] = useState("");
	const [isCodeSent, setIsCodeSent] = useState(false);
	const [isCodeVerified, setIsCodeVerified] = useState(false);
	const [listItems, setListItems] = useState([]);
	const handleFirstNameChange = (e) => {
		setFirstName(e.target.value);
	};

	const handleLastNameChange = (e) => {
		setLastName(e.target.value);
	};

	const handleEmailChange = (e) => {
		setEmail(e.target.value);
	};

	const handlePasswordChange = (e) => {
		setPassword(e.target.value);
	};

	const handleConfirmPasswordChange = (e) => {
		setConfirmPassword(e.target.value);
	};

	const handleVerificationCodeChange = (e) => {
		setVerificationCode(e.target.value);
	};
	const handleAddButton = () => {
		setListItems([
			...listItems,
			<ListItem
				key={listItems.length}
				index={listItems.length}
				onDelete={handleDelete}
			/>,
		]);
	};

	const handleDelete = (index) => {
		setListItems((prevListItems) => {
			const updatedListItems = [...prevListItems];
			updatedListItems.splice(index, 1);
			return updatedListItems;
		});
	};

	const handleSubmit = (event) => {
		event.preventDefault();

		if (password !== confirmPassword) {
			console.log("Passwords do not match");
			return;
		}

		if (!isCodeVerified) {
			console.log("Please verify the verification code");
			return;
		}

		// Additional logic for submitting the form and handling the data on the server-side
		const formData = {
			firstName,
			lastName,
			email,
			// ... other form data
		};

		// Send the form data to the server for further processing

		console.log(formData);
	};

	useEffect(() => {
		handleAddButton(); // Add initial content source list item
	}, []);

	return (
		<div className="signup">
			<div className="form-container">
				<p className="already-signed-in">
					Already have an account? <a href="sign-in.html">Sign in</a>
				</p>
				<form className="signup-form" onSubmit={handleSubmit}>
					<div className="name-wrapper">
						<input
							type="text"
							placeholder="First Name"
							name="firstName"
							value={firstName}
							onChange={handleFirstNameChange}
						/>
						<input
							type="text"
							placeholder="Last Name"
							name="lastName"
							value={lastName}
							onChange={handleLastNameChange}
						/>
					</div>
					<label>Email</label>
					<input
						type="text"
						placeholder="Email"
						name="email"
						value={email}
						onChange={handleEmailChange}
					/>

					<label>Interests</label>
					<textarea
						placeholder="Interests"
						name="interests"
						style={{ height: "200px" }}
					></textarea>

					<label>Content Sources</label>
					{/* Existing code for rendering list items */}

					<div className="button-wrapper">
						<button type="button" id="add-button" onClick={handleAddButton}>
							+
						</button>
					</div>

					<div className="button-wrapper">
						<input type="submit" value="Sign Up" />
					</div>
				</form>
			</div>
		</div>
	);
}

export default Signup;
