import React, { useState, useEffect } from "react";
import Navbar from "../Components/Navbar";
import Footer from "../Components/Footer";
import { useParams } from "react-router-dom";
import "./styles/NewsTemplate.css";

function NewsTemplate() {
	const { flavor } = useParams();
	let title = flavor.charAt().toUpperCase() + flavor.slice(1);

	const [preferences, setPreferences] = useState({
		personality: "straightforward and serious",
		topics: ["finance", "tech", "startups"],
		sources: ["nytimes.com", "politico.com", "wsj.com"],
		color: "red",
	});

	const [unsavedChanges, setUnsavedChanges] = useState(false);
	const [editedPreferences, setEditedPreferences] = useState(preferences);

	const handlePreferenceChange = (key, value) => {
		setEditedPreferences((prevPreferences) => ({
			...prevPreferences,
			[key]: value,
		}));
		setUnsavedChanges(true);
	};

	const handleSave = () => {
		setPreferences(editedPreferences);
		setUnsavedChanges(false);
		// Send preferences to backend
	};

	return (
		<div>
			<Navbar backgroundColor="#fefbf0" isSignedIn={true} />
			<div className="news-template">
				<h1>Settings:</h1>
				<h2>{title}</h2>
				<div className="preference-wrapper">
					<label>
						Personality:
						<input
							type="text"
							value={editedPreferences.personality}
							onChange={(e) =>
								handlePreferenceChange("personality", e.target.value)
							}
						/>
					</label>
					<label>
						Topics:
						<input
							type="text"
							value={editedPreferences.topics.join(", ")}
							onChange={(e) =>
								handlePreferenceChange(
									"topics",
									e.target.value.split(",").map((t) => t.trim())
								)
							}
						/>
					</label>
					<label>
						Sources:
						<input
							type="text"
							value={editedPreferences.sources.join(", ")}
							onChange={(e) =>
								handlePreferenceChange(
									"sources",
									e.target.value.split(",").map((s) => s.trim())
								)
							}
						/>
					</label>
					<label>
						Color:
						<input
							type="text"
							value={editedPreferences.color}
							onChange={(e) => handlePreferenceChange("color", e.target.value)}
						/>
					</label>
				</div>
				<div className="button-wrapper">
					<button onClick={handleSave} disabled={!unsavedChanges}>
						Save Preferences
					</button>
					{unsavedChanges && <span>Unsaved changes</span>}
				</div>
			</div>
			<Footer backgroundColor="#fefbf0" />
		</div>
	);
}

export default NewsTemplate;
