import { NavLink } from "react-router-dom";
import React, { useState } from "react";
import "./styles/Signup.css";
import { useNavigate } from "react-router-dom";
import Navbar from "../Components/Navbar";
import Footer from "../Components/Footer";

function ListItem({
	index,
	onDelete,
	handleSourceChange,
	handleTextChange,
	source,
	text,
}) {
	let placeholder = ""; // Default placeholder
	if (source === "news") {
		placeholder = "Ex: nytimes.com, ...";
	} else if (source === "reddit") {
		placeholder = "Ex: r/AskReddit, ...";
	} else if (source === "twitter") {
		placeholder = "Ex: @BerkeleySkydeck, ...";
	}

	const handleDelete = () => {
		onDelete(index);
	};

	return (
		<div className="list-item">
			<select name="source[]" value={source} onChange={handleSourceChange}>
				<option value="news">News</option>
				<option value="reddit">Reddit</option>
				<option value="twitter">Twitter</option>
			</select>
			<div style={{ display: "flex", alignItems: "center" }}>
				<input
					type="text"
					name="text[]"
					placeholder={placeholder}
					value={text}
					onChange={handleTextChange}
					required
				/>
			</div>
			<span className="delete-button" onClick={handleDelete}>
				<img src="trash-can.svg" alt="Delete" />
			</span>
		</div>
	);
}

function Signup({ isSignedIn, setIsSignedIn }) {
	const [firstName, setFirstName] = useState("");
	const [lastName, setLastName] = useState("");
	const [email, setEmail] = useState("");
	const [interests, setInterests] = useState("");
	const [listItems, setListItems] = useState([
		{ source: "news", text: "" }, // Initial content source item
	]);
	const navigate = useNavigate();

	const handleFirstNameChange = (e) => {
		setFirstName(e.target.value);
	};

	const handleLastNameChange = (e) => {
		setLastName(e.target.value);
	};

	const handleEmailChange = (e) => {
		setEmail(e.target.value);
	};

	const handleInterestsChange = (e) => {
		setInterests(e.target.value);
	};

	const handleAddButton = () => {
		setListItems((prevListItems) => [
			...prevListItems,
			{ source: "news", text: "" }, // Add a new content source item
		]);
	};

	if (isSignedIn) {
		navigate("/main");
	}

	const handleDelete = (index) => {
		if (listItems.length === 1) {
			return; // Do not delete if there is only one item
		}

		setListItems((prevListItems) => {
			const updatedListItems = [...prevListItems];
			updatedListItems.splice(index, 1);
			return updatedListItems;
		});
	};

	const handleSubmit = (event) => {
		event.preventDefault();

		const formData = {
			firstName,
			lastName,
			email,
			interests: interests.split(",").map((interest) => interest.trim()),
			contentSources: listItems,
		};

		// Send the form data to the server for further processing
		console.log(formData);
		setIsSignedIn(true);
	};

	return (
		<div>
			<Navbar backgroundColor={"#43372e"} />
			<div className="signup">
				<div className="form-container">
					<p className="already-signed-in">
						Already have an account? <a href="sign-in.html">Sign in</a>
					</p>
					<form className="signup-form" onSubmit={handleSubmit}>
						<h1>User</h1>
						<div className="name-wrapper">
							<input
								type="text"
								placeholder="First Name"
								name="firstName"
								value={firstName}
								onChange={handleFirstNameChange}
								required
							/>
							<input
								type="text"
								placeholder="Last Name"
								name="lastName"
								value={lastName}
								onChange={handleLastNameChange}
								required
							/>
						</div>
						<label>Email</label>
						<input
							type="text"
							placeholder="Email"
							name="email"
							value={email}
							onChange={handleEmailChange}
							required
						/>
						<h1>Personalization</h1>
						<label>Interests</label>
						<textarea
							placeholder="Tech updates, finance news, formula one..."
							name="interests"
							value={interests}
							onChange={handleInterestsChange}
							style={{ height: "200px" }}
							required
						></textarea>

						<label>Content Sources</label>
						<textarea
							placeholder="nytimes.com, politico.com..."
							name="interests"
							value={interests}
							onChange={handleInterestsChange}
							style={{ height: "200px" }}
							required
						></textarea>

						<label>Daily Digest Personality</label>
						<input
							type="text"
							placeholder="funny and humorous"
							name="email"
							value={email}
							onChange={handleEmailChange}
							required
						/>
						<div className="button-wrapper">
							<input type="submit" value="Sign Up" />
						</div>
					</form>
				</div>
			</div>
			<Footer backgroundColor={"#43372e"} />
		</div>
	);
}

export default Signup;
