import React, { useState } from "react";
import "./styles/Main.css";
import Navbar from "../Components/Navbar";
import Footer from "../Components/Footer";

const Main = (isSignedIn) => {
	const [selectedSquare, setSelectedSquare] = useState(null);
	const [firstName, setFirstName] = useState("Michael");
	const [hoveredSquare, setHoveredSquare] = useState(null);

	const flavors = [
		{
			id: 1,
			flavor: "Caramel",
			title: "Business",
			description: "General information about business news.",
			color: "#D4A15D",
		},
		{
			id: 2,
			flavor: "Mocha",
			title: "Arts",
			description: "General information about arts news.",
			color: "#8B5742",
		},
		{
			id: 3,
			flavor: "Hazelnut",
			title: "Technology",
			description: "General information about technology news.",
			color: "#B38967",
		},
		{
			id: 4,
			flavor: "Cinnamon",
			title: "Science",
			description: "General information about science news.",
			color: "#BC6B38",
		},
		{
			id: 5,
			flavor: "Pumpkin Spice",
			title: "Health",
			description: "General information about health news.",
			color: "#A15233",
		},
		{
			id: 6,
			flavor: "Vanilla",
			title: "Lifestyle",
			description: "General information about lifestyle news.",
			color: "#D5C2B1",
		},
	];

	const handleSquareClick = (flavorId) => {
		// setSelectedSquare((prevSelectedSquare) =>
		// 	prevSelectedSquare === flavorId ? null : flavorId
		// );
	};

	const handleSquareHover = (flavorId) => {
		setHoveredSquare(flavorId);
	};

	const handleSquareLeave = () => {
		setHoveredSquare(null);
	};

	return (
		<div>
			<Navbar backgroundColor={"#fefbf0"} isSignedIn={isSignedIn} />
			<div className="main-container">
				<div className="main">
					{/* Left-aligned greeting */}
					<h1 className="greeting">Hello, {firstName}!</h1>
					<h1 className="welcome">Welcome to your Profile</h1>

					{/* 2x3 grid of squares */}
					<div className="grid">
						{flavors.map((flavor) => (
							<div
								key={flavor.id}
								className={`square ${
									selectedSquare === flavor.id ? "selected" : ""
								} ${hoveredSquare === flavor.id ? "hovered" : ""}`}
								style={{
									backgroundColor:
										hoveredSquare === flavor.id ? "transparent" : flavor.color,
									color: hoveredSquare === flavor.id ? flavor.color : "#fefbf0",
									outline: hoveredSquare === flavor.id ? "10px solid" : "none",
									outlineOffset: hoveredSquare === flavor.id ? "-10px" : "0",
								}}
								onClick={() => handleSquareClick(flavor.id)}
								onMouseEnter={() => handleSquareHover(flavor.id)}
								onMouseLeave={handleSquareLeave}
							>
								<div className="content">
									<div className="flavor">{flavor.flavor}</div>
								</div>
							</div>
						))}
					</div>
				</div>
			</div>
			<Footer backgroundColor={"#fefbf0"} />
		</div>
	);
};

export default Main;
