import React, { useState } from "react";
import Navbar from "../Components/Navbar";
import Footer from "../Components/Footer";
import { useParams } from "react-router-dom";
import { SliderPicker } from "react-color";
import "./styles/NewsTemplate.css";

function NewsTemplate() {
	const { flavor } = useParams();
	const [title, setTitle] = useState(
		flavor.charAt(0).toUpperCase() + flavor.slice(1)
	);
	const [preferences, setPreferences] = useState({
		title: flavor.charAt(0).toUpperCase() + flavor.slice(1),
		personality: "straightforward and serious",
		topics: ["finance", "tech", "startups"],
		sources: ["nytimes.com", "politico.com", "wsj.com"],
		color: { r: 0, g: 0, b: 0 },
	});
	const [unsavedChanges, setUnsavedChanges] = useState(false);

	const handleTitleChange = (event) => {
		setTitle(event.target.value);
		setUnsavedChanges(true);
	};

	const handleColorChange = (color) => {
		const { r, g, b } = color.rgb;
		setPreferences((prevPreferences) => ({
			...prevPreferences,
			color: { r, g, b },
		}));
		setUnsavedChanges(true);
	};

	const handleSave = () => {
		setPreferences((prevPreferences) => ({
			...prevPreferences,
			title,
		}));
		setUnsavedChanges(false);
		// Send preferences to backend
	};

	return (
		<div>
			<Navbar backgroundColor="#fefbf0" isSignedIn={true} />
			<div className="news-template">
				<h1 className="title">Settings:</h1>
				<h2>
					<input
						type="text"
						className="editable-input title"
						value={title}
						onChange={handleTitleChange}
					/>
					<hr className="editable-line" />
				</h2>
				<div className="preference-wrapper">
					<label>
						Personality:
						<input
							type="text"
							className="editable-input"
							value={preferences.personality}
							onChange={(e) =>
								setPreferences((prevPreferences) => ({
									...prevPreferences,
									personality: e.target.value,
								}))
							}
						/>
						<hr className="editable-line" />
					</label>
					<label>
						Topics:
						<input
							type="text"
							className="editable-input"
							value={preferences.topics.join(", ")}
							onChange={(e) =>
								setPreferences((prevPreferences) => ({
									...prevPreferences,
									topics: e.target.value.split(",").map((t) => t.trim()),
								}))
							}
						/>
						<hr className="editable-line" />
					</label>
					<label>
						Sources:
						<input
							type="text"
							className="editable-input"
							value={preferences.sources.join(", ")}
							onChange={(e) =>
								setPreferences((prevPreferences) => ({
									...prevPreferences,
									sources: e.target.value.split(",").map((s) => s.trim()),
								}))
							}
						/>
						<hr className="editable-line" />
					</label>
					<div className="color-picker">
						<div className="color-values-wrapper">
							<label className="color-label">Color:</label>
							<div className="color-values">
								<div className="color-rgb">
									RGB: {preferences.color.r}, {preferences.color.g},{" "}
									{preferences.color.b}
								</div>
								<div className="color-hex">{preferences.color.hex}</div>
							</div>
						</div>
						<div className="color-picker-container">
							<SliderPicker
								color={preferences.color}
								onChange={handleColorChange}
							/>
						</div>
					</div>
				</div>
				<div className="button-wrapper">
					<button onClick={handleSave} disabled={!unsavedChanges}>
						Save Preferences
					</button>
					{unsavedChanges && <span>Unsaved changes</span>}
				</div>
			</div>
			<Footer backgroundColor="#fefbf0" />
		</div>
	);
}

export default NewsTemplate;
